 *
 * @private
 */
class DrupalImageUpload extends Plugin {
  /**
   * @inheritdoc
   */
  static get requires() {
    return [DrupalFileRepository, DrupalImageUploadEditing];
  }

  /**
   * @inheritdoc
   */
