(function ($, window, Drupal) {
  Drupal.behaviors.insertTest = {
    attach(context) {
      $(once('ajax-insert', '.ajax-insert')).on('click', (event) => {
        event.preventDefault();
        const ajaxSettings = {
          url: event.currentTarget.getAttribute('href'),
