// Erweiterte Game State für 100-Level System
interface ExtendedGameState {
  currentLevel: number;
  currentChapter: number;
  completedLevels: number[];
  completedChapters: number[];
  totalPlayTime: number;
  globalScores: {
    empathy: number;
    humanRights: number;
    participation: number;
    civilCourage: number;
  };
  skillPoints: {
    empathy: number;
    rights: number;
    participation: number;
    courage: number;
  };
  unlockedSkills: string[];
  achievements: string[];
  playerLevel: number;
  experiencePoints: number;
  gamePhase: 'intro' | 'chapter-select' | 'playing' | 'level-complete' | 'chapter-complete' | 'endgame';
  selectedChoice: number | null;
  showFeedback: boolean;
  multiplayerMode: boolean;
  worldState: {
    regions: RegionState[];
    globalCrisisLevel: number;
    bridgeIntegrity: number;
  };
}

interface RegionState {
  id: number;
  state: 'thriving' | 'stable' | 'fragile' | 'crisis';
  bridgeHealth: number;
  unlocked: boolean;
}

interface SkillTreeItem {
  id: string;
  name: string;
  description: string;
  category: 'empathy' | 'rights' | 'participation' | 'courage';
  cost: number;
  unlocked: boolean;
  prerequisite?: string;
}

