            </CommandItem>
          </CommandGroup>

          <CommandSeparator />

          <CommandGroup heading="Aktionen">
            <CommandItem
              onSelect={() => handleSelect(() => onNavigate('#join'))}
            >
              <Crown className="mr-2 h-4 w-4" />
              <span>Mitglied werden</span>
