            </CommandItem>
          </CommandGroup>

          {isAuthenticated && (
            <>
              <CommandSeparator />
              <CommandGroup heading="Mein Profil">
                <CommandItem
                  onSelect={() => handleSelect(() => onAction('profile'))}
                >
                  <User className="mr-2 h-4 w-4" />
