          )}

          {userRole === 'admin' && (
            <>
              <CommandSeparator />
              <CommandGroup heading="Admin">
                <CommandItem
                  onSelect={() => handleSelect(() => onNavigate('#admin-dashboard'))}
                >
                  <Settings className="mr-2 h-4 w-4" />
