                </CommandItem>
              </CommandGroup>
            </>
          )}

          <CommandSeparator />

          <CommandGroup heading="Einstellungen">
            <CommandItem
              onSelect={() => handleSelect(() => onAction('toggle-theme'))}
            >
              <Moon className="mr-2 h-4 w-4" />
              <span>Theme wechseln</span>
            </CommandItem>
          </CommandGroup>
        </CommandList>
      </CommandDialog>
    </>
  );
}

