    elder: { bgColor: "#fef7ed", iconColor: "#ea580c", symbol: "👴" },
    parent: { bgColor: "#fdf2f8", iconColor: "#ec4899", symbol: "👨‍👩‍👧‍👦" },
    official: { bgColor: "#f8fafc", iconColor: "#475569", symbol: "🏛️" },
    activist: { bgColor: "#f3e8ff", iconColor: "#8b5cf6", symbol: "✊" },
