        </defs>

        {/* Avatar Background */}
        <circle cx="24" cy="24" r="22" fill={currentRole.bgColor} stroke={currentRole.iconColor} strokeWidth="2"/>

        {/* Role Icon */}
        <text x="24" y="30" textAnchor="middle" fontSize="16" fill={currentRole.iconColor}>
          {currentRole.symbol}
