        {/* Emotion Indicator */}
        <circle cx="36" cy="12" r="6" fill={emotionColors[emotion]}>
          <text x="36" y="15" textAnchor="middle" fontSize="8" fill="white">
            {emotion === 'happy' ? '😊' : 
             emotion === 'concerned' ? '😟' : 
