    }
  });

  console.log(
    `${GREEN}✓${RESET} ${Object.keys(tokens.typography).length} Typography Tokens validiert`
  );
}

// 6. Validiere Spacing
console.log(`\n${BLUE}📋 Schritt 6: Spacing validieren${RESET}`);

if (tokens.spacing) {
  Object.entries(tokens.spacing).forEach(([name, spacingToken]) => {
