/**
 * MINI-GAME 3: Debate Duel
 * Real-time Argumentation vs. AI
 */
class DebateDuel {
  constructor(parent) {
    this.parent = parent;
    this.rounds = 5;
    this.currentRound = 0;
    this.playerScore = 0;
    this.aiScore = 0;
    this.audienceScore = 50; // 0-100, starts neutral
