          const targetId = this.getAttribute('href').substring(1);
          const targetElement = document.getElementById(targetId);

          if (targetElement) {
            e.preventDefault();

            const headerHeight = document.querySelector('.header')?.offsetHeight || 0;
            const navHeight = document.querySelector('.navigation--primary')?.offsetHeight || 0;
            const offset = headerHeight + navHeight + 20; // 20px extra padding

            const targetPosition = targetElement.offsetTop - offset;

            window.scrollTo({
              top: targetPosition,
              behavior: 'smooth',
            });

            // Update focus for accessibility
            targetElement.focus({ preventScroll: true });
          }
        });
      });
    },
  };

  /**
   * Progressive Enhancement for Forms
   */
  Drupal.behaviors.formEnhancements = {
    attach: function (context) {
      // Auto-expand textareas
      const textareas = once('auto-expand', 'textarea', context);

      textareas.forEach(function (textarea) {
        // Set initial height
        autoResize(textarea);

        textarea.addEventListener('input', function () {
          autoResize(this);
        });
      });

      // Add loading states to form buttons
      const forms = once('form-loading', 'form', context);

      forms.forEach(function (form) {
        form.addEventListener('submit', function () {
          const submitButtons = form.querySelectorAll(
            'button[type="submit"], input[type="submit"]'
          );
          submitButtons.forEach(function (button) {
            button.classList.add('btn--loading');
            button.disabled = true;

            // Add loading text if it's a button element
            if (button.tagName === 'BUTTON') {
              button.dataset.originalText = button.textContent;
              button.innerHTML =
                '<span class="btn__icon" aria-hidden="true">⟳</span> ' +
                (button.dataset.loadingText || Drupal.t('Loading...'));
            }
          });
        });
      });
    },
  };

  /**
   * Auto-resize textarea function
   */
  function autoResize(textarea) {
    textarea.style.height = 'auto';
    textarea.style.height = textarea.scrollHeight + 'px';
  }

  /**
   * Intersection Observer for Animation Triggers
   */
  Drupal.behaviors.scrollAnimations = {
    attach: function (context) {
      if (!window.IntersectionObserver) return;

      const animatedElements = once('scroll-animate', '[data-animate]', context);

      if (animatedElements.length === 0) return;

      const observer = new IntersectionObserver(
        function (entries) {
          entries.forEach(function (entry) {
            if (entry.isIntersecting) {
              const element = entry.target;
              const animationType = element.dataset.animate;

              element.classList.add('animate-' + animationType);

              // Stop observing once animated
              observer.unobserve(element);
            }
          });
        },
        {
          threshold: 0.1,
          rootMargin: '0px 0px -50px 0px',
        }
      );

      animatedElements.forEach(function (element) {
        observer.observe(element);
      });
    },
  };

  /**
   * Keyboard Navigation Enhancement
   */
  Drupal.behaviors.keyboardNavigation = {
    attach: function (context) {
      // Escape key handling for modals/dropdowns
      document.addEventListener('keydown', function (e) {
        if (e.key === 'Escape') {
          // Close any open modals
          const openModals = document.querySelectorAll('[open][role="dialog"]');
          openModals.forEach(function (modal) {
            modal.close();
          });

          // Close any open dropdowns
          const openDropdowns = document.querySelectorAll('.dropdown--open');
          openDropdowns.forEach(function (dropdown) {
            dropdown.classList.remove('dropdown--open');
          });
        }
      });

      // Focus trap for modals
      const modals = once('focus-trap', '[role="dialog"]', context);

      modals.forEach(function (modal) {
        modal.addEventListener('keydown', function (e) {
          if (e.key === 'Tab') {
            trapFocus(e, modal);
          }
        });
      });
    },
  };

  /**
   * Focus trap helper function
   */
  function trapFocus(e, container) {
    const focusableElements = container.querySelectorAll(
      'button, [href], input, select, textarea, [tabindex]:not([tabindex="-1"])'
    );
    const firstElement = focusableElements[0];
    const lastElement = focusableElements[focusableElements.length - 1];

    if (e.shiftKey) {
      if (document.activeElement === firstElement) {
        e.preventDefault();
        lastElement.focus();
      }
    } else {
      if (document.activeElement === lastElement) {
        e.preventDefault();
        firstElement.focus();
      }
    }
  }

  /**
   * Lazy Loading for Images
   */
  Drupal.behaviors.lazyLoading = {
    attach: function (context) {
      if (!window.IntersectionObserver) return;

      const lazyImages = once('lazy-load', 'img[data-src]', context);

      if (lazyImages.length === 0) return;

      const imageObserver = new IntersectionObserver(function (entries) {
        entries.forEach(function (entry) {
          if (entry.isIntersecting) {
            const img = entry.target;
            img.src = img.dataset.src;
            img.classList.add('loaded');
            imageObserver.unobserve(img);
          }
        });
      });

      lazyImages.forEach(function (img) {
        imageObserver.observe(img);
      });
    },
  };

  /**
   * Performance Monitoring
   */
  Drupal.behaviors.performanceMonitoring = {
    attach: function (context, settings) {
      // Only run on document ready, not AJAX contexts
      if (context !== document) return;

      // Log Core Web Vitals if available
      if ('web-vitals' in window) {
        window.webVitals.getCLS(function (metric) {
          console.log('CLS:', metric.value);
        });

        window.webVitals.getFID(function (metric) {
          console.log('FID:', metric.value);
        });

        window.webVitals.getLCP(function (metric) {
          console.log('LCP:', metric.value);
        });
      }

      // Simple performance timing
      window.addEventListener('load', function () {
        setTimeout(function () {
          const perfData = performance.timing;
          const loadTime = perfData.loadEventEnd - perfData.navigationStart;
          console.log('Page load time:', loadTime + 'ms');
        }, 0);
      });
    },
  };
})(Drupal, once);

// Authentication Handler für Login/Register Forms

class AuthHandler {
  constructor() {
    this.init();
  }

  init() {
    // DOM ready check
    if (document.readyState === 'loading') {
      document.addEventListener('DOMContentLoaded', () => this.bindEvents());
    } else {
      this.bindEvents();
    }
  }

  bindEvents() {
    // Login Form
    const loginForm = document.getElementById('loginForm');
    if (loginForm) {
      loginForm.addEventListener('submit', e => this.handleLogin(e));
    }

    // Register Form
    const registerForm = document.getElementById('registerForm');
    if (registerForm) {
      registerForm.addEventListener('submit', e => this.handleRegister(e));
    }

    // Form Toggle Links
    const showRegisterLink = document.getElementById('showRegisterLink');
    const showLoginLink = document.getElementById('showLoginLink');

    if (showRegisterLink) {
      showRegisterLink.addEventListener('click', e => this.toggleForms(e, 'register'));
    }

    if (showLoginLink) {
      showLoginLink.addEventListener('click', e => this.toggleForms(e, 'login'));
    }

    // Password Visibility Toggle
    this.initPasswordToggle('toggleLoginPassword', 'loginPassword');
    this.initPasswordToggle('toggleRegisterPassword', 'registerPassword');

    // Password Strength Checker
    const registerPassword = document.getElementById('registerPassword');
    if (registerPassword) {
      registerPassword.addEventListener('input', () => this.checkPasswordStrength());
      registerPassword.addEventListener('keyup', () => this.validatePasswordMatch());
    }

    // Password Confirmation Validation
    const registerPasswordConfirm = document.getElementById('registerPasswordConfirm');
    if (registerPasswordConfirm) {
      registerPasswordConfirm.addEventListener('input', () => this.validatePasswordMatch());
      registerPasswordConfirm.addEventListener('keyup', () => this.validatePasswordMatch());
    }

    // Check if already logged in
    this.checkAuthStatus();
  }

  async handleLogin(event) {
    event.preventDefault();

    const form = event.target;
    const email = document.getElementById('loginEmail').value.trim();
    const password = document.getElementById('loginPassword').value;
    const rememberMe = document.getElementById('rememberMe').checked;

    // Validation
    if (!this.validateEmail(email)) {
      this.showFieldError('loginEmail', 'Bitte geben Sie eine gültige E-Mail-Adresse ein.');
      return;
    }

    if (!password) {
      this.showFieldError('loginPassword', 'Bitte geben Sie Ihr Passwort ein.');
      return;
    }

    // Clear previous errors
    this.clearFieldErrors();

    // Show loading state
    this.setLoadingState(form, true, 'Anmeldung läuft...');

    try {
      // API call
      const result = await window.crmApi.login(email, password);

      if (result.success) {
        this.showAlert('Erfolgreich angemeldet!', 'success');

        // Save remember me preference
        if (rememberMe) {
          localStorage.setItem('remember_me', 'true');
        } else {
          localStorage.removeItem('remember_me');
        }

        // Redirect to member area
        setTimeout(() => {
          window.location.href = 'member-area.html';
        }, 1000);
      } else {
        this.showAlert(result.error, 'danger');
      }
    } catch (error) {
      this.showAlert(
        'Ein unerwarteter Fehler ist aufgetreten. Bitte versuchen Sie es erneut.',
        'danger'
      );
    } finally {
      this.setLoadingState(form, false);
    }
  }

  async handleRegister(event) {
    event.preventDefault();

    const form = event.target;
    const firstName = document.getElementById('registerFirstName').value.trim();
    const lastName = document.getElementById('registerLastName').value.trim();
    const email = document.getElementById('registerEmail').value.trim();
    const password = document.getElementById('registerPassword').value;
    const passwordConfirm = document.getElementById('registerPasswordConfirm').value;
    const privacyConsent = document.getElementById('privacyConsent').checked;
    const newsletterConsent = document.getElementById('newsletterConsent').checked;

    // Validation
    const validationErrors = this.validateRegistrationForm({
      firstName,
      lastName,
      email,
      password,
      passwordConfirm,
      privacyConsent,
    });

    if (validationErrors.length > 0) {
      validationErrors.forEach(error => {
        this.showFieldError(error.field, error.message);
      });
      return;
    }

    // Clear previous errors
    this.clearFieldErrors();

    // Show loading state
    this.setLoadingState(form, true, 'Registrierung läuft...');

    try {
      // Prepare user data
      const userData = {
        first_name: firstName,
        last_name: lastName,
        email: email,
        password: password,
        privacy_consent: privacyConsent,
        newsletter_consent: newsletterConsent,
      };

      // API call
      const result = await window.crmApi.register(userData);

      if (result.success) {
        this.showAlert('Registrierung erfolgreich! Sie können sich jetzt anmelden.', 'success');

        // Switch to login form
        setTimeout(() => {
          this.toggleForms(null, 'login');

          // Pre-fill email
          document.getElementById('loginEmail').value = email;
        }, 2000);
      } else {
        this.showAlert(result.error, 'danger');
      }
    } catch (error) {
      this.showAlert(
        'Ein unerwarteter Fehler ist aufgetreten. Bitte versuchen Sie es erneut.',
        'danger'
      );
    } finally {
      this.setLoadingState(form, false);
    }
  }

  toggleForms(event, targetForm) {
    if (event) {
      event.preventDefault();
    }

    const loginForm = document.getElementById('login-form');
    const registerForm = document.getElementById('register-form');

    if (targetForm === 'register') {
      loginForm?.classList.add('d-none');
      registerForm?.classList.remove('d-none');
    } else {
      registerForm?.classList.add('d-none');
      loginForm?.classList.remove('d-none');
    }

    // Clear form errors
    this.clearFieldErrors();
  }

  initPasswordToggle(buttonId, inputId) {
    const toggleButton = document.getElementById(buttonId);
    const passwordInput = document.getElementById(inputId);

    if (!toggleButton || !passwordInput) return;

    toggleButton.addEventListener('click', () => {
      const isPassword = passwordInput.type === 'password';
      passwordInput.type = isPassword ? 'text' : 'password';

      const icon = toggleButton.querySelector('i');
      if (icon) {
        icon.className = isPassword ? 'bi bi-eye-slash' : 'bi bi-eye';
      }
    });
  }

  checkPasswordStrength() {
    const password = document.getElementById('registerPassword').value;
    const strengthBar = document.getElementById('passwordStrengthBar');
    const strengthText = document.getElementById('strengthLevel');

    if (!password) {
      strengthBar.style.width = '0%';
      strengthBar.className = 'progress-bar';
      strengthText.textContent = '-';
      return;
    }

    let strength = 0;
    const checks = [
      password.length >= 8,
      /[a-z]/.test(password),
      /[A-Z]/.test(password),
      /[0-9]/.test(password),
      /[^a-zA-Z0-9]/.test(password),
    ];

    strength = checks.filter(check => check).length;

    const levels = ['Sehr schwach', 'Schwach', 'Mittel', 'Stark', 'Sehr stark'];
    const colors = ['bg-danger', 'bg-warning', 'bg-info', 'bg-success', 'bg-primary'];
    const widths = ['20%', '40%', '60%', '80%', '100%'];

    strengthBar.style.width = widths[strength - 1] || '0%';
    strengthBar.className = `progress-bar ${colors[strength - 1] || ''}`;
    strengthText.textContent = levels[strength - 1] || '-';
  }

  validatePasswordMatch() {
    const password = document.getElementById('registerPassword').value;
    const passwordConfirm = document.getElementById('registerPasswordConfirm').value;
    const confirmField = document.getElementById('registerPasswordConfirm');

    if (passwordConfirm && password !== passwordConfirm) {
      this.showFieldError('registerPasswordConfirm', 'Die Passwörter stimmen nicht überein.');
      return false;
    } else if (passwordConfirm && password === passwordConfirm) {
      this.clearFieldError('registerPasswordConfirm');
      return true;
    }
    return true;
  }

  validateRegistrationForm(data) {
    const errors = [];

    if (!data.firstName) {
      errors.push({ field: 'registerFirstName', message: 'Bitte geben Sie Ihren Vornamen ein.' });
    }

    if (!data.lastName) {
      errors.push({ field: 'registerLastName', message: 'Bitte geben Sie Ihren Nachnamen ein.' });
    }

    if (!this.validateEmail(data.email)) {
      errors.push({
        field: 'registerEmail',
        message: 'Bitte geben Sie eine gültige E-Mail-Adresse ein.',
      });
    }

    if (!data.password || data.password.length < 8) {
      errors.push({
        field: 'registerPassword',
        message: 'Das Passwort muss mindestens 8 Zeichen lang sein.',
      });
    }

    if (data.password !== data.passwordConfirm) {
      errors.push({
        field: 'registerPasswordConfirm',
        message: 'Die Passwörter stimmen nicht überein.',
      });
    }

    if (!data.privacyConsent) {
      errors.push({
        field: 'privacyConsent',
        message: 'Sie müssen der Datenschutzerklärung zustimmen.',
      });
    }

    return errors;
  }

  validateEmail(email) {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  }

  showFieldError(fieldId, message) {
    const field = document.getElementById(fieldId);
    if (!field) return;

    field.classList.add('is-invalid');

    const feedback = field.parentElement.querySelector('.invalid-feedback');
    if (feedback) {
      feedback.textContent = message;
    }
  }

  clearFieldError(fieldId) {
    const field = document.getElementById(fieldId);
    if (!field) return;

    field.classList.remove('is-invalid');
  }

  clearFieldErrors() {
    const invalidFields = document.querySelectorAll('.is-invalid');
    invalidFields.forEach(field => field.classList.remove('is-invalid'));
  }

  setLoadingState(form, loading, text = '') {
    const submitButton = form.querySelector('button[type="submit"]');
    if (!submitButton) return;

    const buttonText = submitButton.querySelector('.button-text');
    const buttonLoading = submitButton.querySelector('.button-loading');

    if (loading) {
      submitButton.disabled = true;
      buttonText?.classList.add('d-none');
      buttonLoading?.classList.remove('d-none');

      if (text && buttonLoading) {
        const loadingText = buttonLoading.querySelector('span:not(.spinner-border)');
        if (loadingText) loadingText.textContent = text;
      }
    } else {
      submitButton.disabled = false;
      buttonText?.classList.remove('d-none');
      buttonLoading?.classList.add('d-none');
    }
  }

  showAlert(message, type = 'info') {
    // Remove existing alerts
    const existingAlerts = document.querySelectorAll('.auth-alert');
    existingAlerts.forEach(alert => alert.remove());

    // Create new alert
    const alertDiv = document.createElement('div');
    alertDiv.className = `alert alert-${type} alert-dismissible fade show auth-alert`;
    alertDiv.setAttribute('role', 'alert');

    alertDiv.innerHTML = `
            ${message}
            <button type="button" class="btn-close" data-bs-dismiss="alert" aria-label="Close"></button>
        `;

    // Insert at the top of the form container
    const authSection = document.querySelector('.auth-section');
    if (authSection) {
      const container = authSection.querySelector('.container');
      if (container) {
        container.insertBefore(alertDiv, container.firstChild);
      }
    }

    // Auto-dismiss after 5 seconds for success messages
    if (type === 'success') {
      setTimeout(() => {
        if (alertDiv.parentNode) {
          alertDiv.remove();
        }
      }, 5000);
    }

    // Scroll to alert
    alertDiv.scrollIntoView({ behavior: 'smooth', block: 'center' });
  }

  checkAuthStatus() {
    // Check if user is already logged in
    if (window.crmApi && window.crmApi.isLoggedIn()) {
      // Redirect to member area if on login page
      if (window.location.pathname.includes('login.html')) {
        window.location.href = 'member-area.html';
      }
    }
  }

  // Forgot Password Handler
  handleForgotPassword() {
    const email = document.getElementById('loginEmail').value.trim();

    if (!email) {
      this.showAlert('Bitte geben Sie zuerst Ihre E-Mail-Adresse ein.', 'warning');
      return;
    }

    if (!this.validateEmail(email)) {
      this.showAlert('Bitte geben Sie eine gültige E-Mail-Adresse ein.', 'danger');
      return;
    }

    // TODO: Implement password reset API call
    this.showAlert(
      'Die Funktion "Passwort zurücksetzen" ist noch nicht verfügbar. Bitte kontaktieren Sie uns per E-Mail.',
      'info'
    );
  }
}

// Initialize when DOM is ready
document.addEventListener('DOMContentLoaded', () => {
  new AuthHandler();

  // Forgot password link handler
  const forgotPasswordLink = document.getElementById('forgotPasswordLink');
  if (forgotPasswordLink) {
    forgotPasswordLink.addEventListener('click', e => {
      e.preventDefault();
      const authHandler = new AuthHandler();
      authHandler.handleForgotPassword();
    });
  }
});

// CRM API Integration für Website
// Hauptklasse für alle CiviCRM API-Aufrufe

class CRMApi {
  constructor() {
    this.baseUrl = 'https://api.menschlichkeit-oesterreich.at';
    this.token = null;
    this.refreshToken = null;
    this.init();
  }

  init() {
    // JWT Token laden (Session bevorzugt, Fallback Local)
    this.token = localStorage.getItem('jwt_token');
    const sessionToken = sessionStorage.getItem('jwt_token');
    if (sessionToken) {
      this.token = sessionToken;
    }
    // Refresh Token laden
    this.refreshToken = localStorage.getItem('refresh_token');
    const sessionRefresh = sessionStorage.getItem('refresh_token');
    if (sessionRefresh) {
      this.refreshToken = sessionRefresh;
    }
    // Token-Gültigkeit prüfen
    if (this.token && this.isTokenExpired(this.token)) {
      this.logout();
    }
  }

  isTokenExpired(token) {
    try {
      const payload = JSON.parse(atob(token.split('.')[1]));
      return payload.exp < Date.now() / 1000;
    } catch (error) {
      return true;
    }
  }

  async refreshAuthToken() {
    if (!this.refreshToken) return false;
    try {
      const res = await fetch(`${this.baseUrl}/auth/refresh`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ refresh_token: this.refreshToken }),
      });
      const data = await res.json().catch(() => ({}));
      if (!res.ok || !data?.success || !data?.data?.token) {
        this.logout();
        return false;
      }
      this._storeTokens(data.data);
      return true;
    } catch (e) {
      console.error('refresh token failed', e);
      this.logout();
      return false;
    }
  }

  async apiRequest(endpoint, options = {}) {
    const url = `${this.baseUrl}${endpoint}`;

    const defaultOptions = {
      headers: {
        'Content-Type': 'application/json',
        ...(this.token && { Authorization: `Bearer ${this.token}` }),
      },
    };

    const requestOptions = {
      ...defaultOptions,
      ...options,
      headers: {
        ...defaultOptions.headers,
        ...options.headers,
      },
    };

    try {
      let response = await fetch(url, requestOptions);
      if (response.status === 401 && this.token && this.refreshToken) {
        const refreshed = await this.refreshAuthToken();
        if (refreshed) {
          // retry once with updated token
          requestOptions.headers['Authorization'] = `Bearer ${this.token}`;
          response = await fetch(url, requestOptions);
        }
      }
      return response;
    } catch (error) {
      console.error('API request error:', error);
      throw error;
    }
  }

  // Authentication
  async login(email, password) {
    try {
      const response = await this.apiRequest('/auth/login', {
        method: 'POST',
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json().catch(() => ({}));
      // Erwartete Struktur: { success, data: { token, refresh_token?, expires_in }, message }
      if (response.ok && data && data.success && data.data && data.data.token) {
        this._storeTokens(data.data);
        return { success: true, data };
      } else {
        const msg = (data && (data.detail || data.message)) || 'Login fehlgeschlagen';
        return { success: false, error: msg };
      }
    } catch (error) {
      console.error('Login error:', error);
      return { success: false, error: 'Verbindungsfehler' };
    }
  }

  async register(userData) {
    // Nutzt Backend /auth/register
    try {
      const response = await this.apiRequest('/auth/register', {
        method: 'POST',
        body: JSON.stringify(userData),
      });
      const data = await response.json().catch(() => ({}));
      if (response.ok && data && data.success) {
        return { success: true, data };
      } else {
        const msg = (data && (data.detail || data.message)) || 'Registrierung fehlgeschlagen';
        return { success: false, error: msg };
      }
    } catch (error) {
      console.error('Registration error:', error);
      return { success: false, error: 'Verbindungsfehler' };
    }
  }

  logout() {
    this.token = null;
    try {
      sessionStorage.removeItem('jwt_token');
    } catch {}
    try {
      sessionStorage.removeItem('refresh_token');
    } catch {}
    localStorage.removeItem('jwt_token');
    localStorage.removeItem('refresh_token');
    localStorage.removeItem('user_data');

    // Redirect to login if on protected page
    if (window.location.pathname.includes('member-area')) {
      window.location.href = 'login.html';
    }
  }

  // Contact Management
  async createContact(contactData) {
    try {
      const response = await this.apiRequest('/contacts/create', {
        method: 'POST',
        body: JSON.stringify(contactData),
      });
      const data = await response.json().catch(() => ({}));
      if (response.ok && data && data.success) {
        return { success: true, data };
      } else {
        const msg =
          (data && (data.detail || data.message)) || 'Kontakt konnte nicht erstellt werden';
        return { success: false, error: msg };
      }
    } catch (error) {
      console.error('Create contact error:', error);
      return { success: false, error: 'Verbindungsfehler' };
    }
  }

  _storeTokens(tokenData) {
    this.token = tokenData.token;
    this.refreshToken = tokenData.refresh_token || this.refreshToken || null;
    // Session speichern
    try {
      sessionStorage.setItem('jwt_token', this.token);
    } catch {}
    if (this.refreshToken) {
      try {
        sessionStorage.setItem('refresh_token', this.refreshToken);
      } catch {}
    }
    // Optional persistent
    const remember = localStorage.getItem('remember_me') === 'true';
    if (remember) {
      localStorage.setItem('jwt_token', this.token);
      if (this.refreshToken) localStorage.setItem('refresh_token', this.refreshToken);
    } else {
      localStorage.removeItem('jwt_token');
      localStorage.removeItem('refresh_token');
    }
  }

  async getContact(contactId) {
    try {
      const response = await this.apiRequest(`/contacts/${contactId}`);

      if (response.ok) {
        const data = await response.json();
        return { success: true, data };
      } else {
        const error = await response.json();
        return { success: false, error: error.detail || 'Kontakt nicht gefunden' };
      }
    } catch (error) {
