document.addEventListener('DOMContentLoaded', () => {
  new SepaHandler();
});

#!/usr/bin/env node
import { spawn } from 'node:child_process';
import { mkdirSync, readFileSync } from 'node:fs';
import { resolve } from 'node:path';

const PORT = process.env.WEB_PREVIEW_PORT || '4180';
const URL = `http://localhost:${PORT}/index.html`;
const REPORT_DIR = resolve(process.cwd(), '.lighthouse');
const REPORT_BASE = resolve(REPORT_DIR, 'report');

function run(cmd, args, opts = {}) {
  return new Promise((resolvePromise, reject) => {
    const p = spawn(cmd, args, { stdio: 'inherit', shell: process.platform === 'win32', ...opts });
    p.on('error', reject);
    p.on('exit', (code) => (code === 0 ? resolvePromise(code) : reject(new Error(`${cmd} exited ${code}`))));
  });
}

async function waitForUrl(url, timeoutMs = 15000) {
  const start = Date.now();
  while (Date.now() - start < timeoutMs) {
    try {
      const res = await fetch(url, { method: 'HEAD' });
      if (res.ok) return true;
    } catch {}
    await new Promise((r) => setTimeout(r, 300));
  }
  throw new Error(`Timeout waiting for ${url}`);
}

async function main() {
  mkdirSync(REPORT_DIR, { recursive: true });

  // start static server
  const server = spawn('npx', ['http-server', '.', '-p', PORT, '-c-1'], { cwd: process.cwd(), stdio: 'inherit', shell: process.platform === 'win32' });
  try {
    await waitForUrl(URL);
    await run('npx', [
      'lighthouse',
      URL,
      '--config-path=./lighthouse.config.cjs',
      '--quiet',
      '--output=json',
      '--output=html',
      `--output-path=${REPORT_BASE}`,
    ], { cwd: process.cwd() });
  } finally {
    server.kill('SIGTERM');
  }

  try {
    const reportJson = readFileSync(`${REPORT_BASE}.report.json`, 'utf-8');
    const data = JSON.parse(reportJson);
    const perf = data.categories?.performance?.score ?? 0;
    const a11y = data.categories?.accessibility?.score ?? 0;
    const bp = data.categories?.['best-practices']?.score ?? 0;
    const seo = data.categories?.seo?.score ?? 0;
    console.log(`Scores: P=${perf} A11y=${a11y} BP=${bp} SEO=${seo}`);
    if (perf < 0.9 || a11y < 0.95 || bp < 0.95 || seo < 0.9) {
      console.error('Budget/Score thresholds nicht erfüllt. Siehe .lighthouse/report.report.html');
      process.exit(1);
    }
  } catch (e) {
    console.warn('Konnte Report nicht prüfen:', e.message);
  }
}

main().catch((e) => {
  console.error(e);
  process.exit(1);
});

/**
 * Service Worker für Menschlichkeit Österreich Website
 * Bietet Offline-Funktionalität und Performance-Verbesserungen
 */

const CACHE_NAME = 'menschlichkeit-v1.0.0';
const STATIC_CACHE = 'menschlichkeit-static-v1.0.0';
const DYNAMIC_CACHE = 'menschlichkeit-dynamic-v1.0.0';

// Assets die gecacht werden sollen
const STATIC_ASSETS = [
  '/',
  '/index.html',
  '/styles-optimized.css',
  '/assets/js/main-optimized.js',
  '/manifest.json',

  // Bootstrap CDN Assets
  'https://cdn.jsdelivr.net/npm/bootstrap@5.3.2/dist/css/bootstrap.min.css',
  'https://cdn.jsdelivr.net/npm/bootstrap@5.3.2/dist/js/bootstrap.bundle.min.js',
  'https://cdn.jsdelivr.net/npm/bootstrap-icons@1.11.1/font/bootstrap-icons.min.css',

  // Fonts
  'https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700&display=swap',

  // Offline fallback page
  '/offline.html',
];

// URLs die nicht gecacht werden sollen
const EXCLUDE_URLS = ['/admin', '/api/', 'analytics', 'gtag', 'google-analytics'];

/* ===== INSTALLATION ===== */
self.addEventListener('install', event => {
  console.log('[SW] Installing service worker...');

  event.waitUntil(
    caches
      .open(STATIC_CACHE)
      .then(cache => {
        console.log('[SW] Precaching static assets');
        return cache.addAll(STATIC_ASSETS);
      })
      .then(() => {
        console.log('[SW] Installation complete');
        return self.skipWaiting();
      })
      .catch(error => {
        console.error('[SW] Installation failed:', error);
      })
  );
});

/* ===== ACTIVATION ===== */
self.addEventListener('activate', event => {
  console.log('[SW] Activating service worker...');

  event.waitUntil(
    Promise.all([
      // Cleanup old caches
      caches.keys().then(cacheNames => {
        return Promise.all(
          cacheNames
            .filter(cacheName => {
              return cacheName !== STATIC_CACHE && cacheName !== DYNAMIC_CACHE;
            })
            .map(cacheName => {
              console.log('[SW] Deleting old cache:', cacheName);
              return caches.delete(cacheName);
            })
        );
      }),

      // Take control of all pages
      self.clients.claim(),
    ]).then(() => {
      console.log('[SW] Activation complete');
    })
  );
});

/* ===== FETCH HANDLING ===== */
self.addEventListener('fetch', event => {
  const requestUrl = new URL(event.request.url);

  // Skip non-GET requests
  if (event.request.method !== 'GET') {
    return;
  }

  // Skip excluded URLs
  if (EXCLUDE_URLS.some(exclude => requestUrl.href.includes(exclude))) {
    return;
  }

  // Handle different types of requests
  if (isStaticAsset(event.request)) {
    event.respondWith(handleStaticAsset(event.request));
  } else if (isApiRequest(event.request)) {
    event.respondWith(handleApiRequest(event.request));
  } else if (isNavigationRequest(event.request)) {
    event.respondWith(handleNavigationRequest(event.request));
  } else {
    event.respondWith(handleDynamicRequest(event.request));
  }
});

/* ===== REQUEST HANDLERS ===== */

// Static Assets (Cache First Strategy)
function handleStaticAsset(request) {
  return caches
    .match(request)
    .then(cachedResponse => {
      if (cachedResponse) {
        return cachedResponse;
      }

      return fetch(request).then(networkResponse => {
        if (networkResponse.ok) {
          const responseClone = networkResponse.clone();
          caches.open(STATIC_CACHE).then(cache => cache.put(request, responseClone));
        }
        return networkResponse;
      });
    })
    .catch(() => {
      console.log('[SW] Static asset fetch failed:', request.url);
      return new Response('Asset not available offline', {
        status: 503,
        statusText: 'Service Unavailable',
      });
    });
}

// API Requests (Network First Strategy)
function handleApiRequest(request) {
  return fetch(request)
    .then(networkResponse => {
      if (networkResponse.ok) {
        const responseClone = networkResponse.clone();
        caches.open(DYNAMIC_CACHE).then(cache => cache.put(request, responseClone));
      }
      return networkResponse;
    })
    .catch(() => {
      return caches.match(request).then(cachedResponse => {
        if (cachedResponse) {
          return cachedResponse;
        }
        return new Response(
          JSON.stringify({
            error: 'API not available offline',
            message: 'Please check your internet connection',
          }),
          {
            status: 503,
            statusText: 'Service Unavailable',
            headers: { 'Content-Type': 'application/json' },
          }
        );
      });
    });
}

// Navigation Requests (Stale While Revalidate)
function handleNavigationRequest(request) {
  return caches.match(request).then(cachedResponse => {
    const networkFetch = fetch(request)
      .then(networkResponse => {
        if (networkResponse.ok) {
          const responseClone = networkResponse.clone();
          caches.open(DYNAMIC_CACHE).then(cache => cache.put(request, responseClone));
        }
        return networkResponse;
      })
      .catch(() => {
        // Fallback zu Offline-Seite
        return caches.match('/offline.html');
      });

    return cachedResponse || networkFetch;
  });
}

// Dynamic Content (Network First with Cache Fallback)
function handleDynamicRequest(request) {
  return fetch(request)
    .then(networkResponse => {
      if (networkResponse.ok) {
        const responseClone = networkResponse.clone();
        caches.open(DYNAMIC_CACHE).then(cache => {
          // Cleanup cache wenn zu groß
          limitCacheSize(cache, 50);
          return cache.put(request, responseClone);
        });
      }
      return networkResponse;
    })
    .catch(() => {
      return caches.match(request);
    });
}

/* ===== HELPER FUNCTIONS ===== */

function isStaticAsset(request) {
  const url = new URL(request.url);
  return (
    url.pathname.includes('/assets/') ||
    url.pathname.includes('/styles') ||
    url.pathname.includes('.css') ||
    url.pathname.includes('.js') ||
    url.pathname.includes('.woff') ||
    url.pathname.includes('.woff2') ||
    url.hostname.includes('cdn.jsdelivr.net') ||
    url.hostname.includes('fonts.googleapis.com') ||
    url.hostname.includes('fonts.gstatic.com')
  );
}

function isApiRequest(request) {
  const url = new URL(request.url);
  return (
    url.pathname.includes('/api/') ||
    url.pathname.includes('/contact') ||
    request.headers.get('content-type')?.includes('application/json')
  );
}

function isNavigationRequest(request) {
  return (
    request.mode === 'navigate' ||
    (request.method === 'GET' && request.headers.get('accept')?.includes('text/html'))
  );
}

// Cache size limiter
function limitCacheSize(cache, maxItems) {
  cache.keys().then(keys => {
    if (keys.length > maxItems) {
      cache.delete(keys[0]).then(() => {
        limitCacheSize(cache, maxItems);
      });
    }
  });
}

